import React, { useState, useEffect } from 'react';
import Navigation from '../components/Navigation';
import Footer from '../components/Footer';

const Projects = () => {
  const [expandedSections, setExpandedSections] = useState({});
  const [activeFilter, setActiveFilter] = useState('all');
  const [filteredProjects, setFilteredProjects] = useState([]);
  const [isScrolled, setIsScrolled] = useState(false);

  const toggleExpand = (section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const projectData = [
    // Your existing project data remains the same
  ];

  // Filter categories based on project content
  const categories = [
    'all', 'MSME Development', 'Leadership Training', 
    'Research & Evaluation', 'SRHR', 'WASH'
  ];

  // Filter projects when category changes
  useEffect(() => {
    if (activeFilter === 'all') {
      setFilteredProjects(projectData);
    } else {
      setFilteredProjects(
        projectData.filter(project => 
          project.description.toLowerCase().includes(activeFilter.toLowerCase()) ||
          project.title.toLowerCase().includes(activeFilter.toLowerCase())
        )
      );
    }
  }, [activeFilter]);

  // Add scroll effect to header
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white">
      <Navigation />

      {/* Hero Section */}
      <section className="relative bg-gray-900 text-white overflow-hidden">
        <div className="absolute inset-0 z-0">
          <img
            src="/image/portfolio/team.png"
            alt="Biruhminds Team"
            className="w-full h-full object-cover object-center"
            style={{ transform: 'scale(1.05)' }}
          />
          <div className="absolute inset-0 bg-gradient-to-t from-gray-900/90 to-primary/30"></div>
          <div className="absolute inset-0 bg-gradient-to-r from-gray-900/70 to-gray-900/20"></div>
        </div>

        <div className="container mx-auto px-6 py-32 text-center relative z-10">
          <h1 className="text-4xl md:text-6xl font-bold mb-6 animate-fade-in">
            Transforming Ethiopia's Economic Landscape
          </h1>
          <p className="text-xl md:text-2xl max-w-3xl mx-auto mb-8 animate-fade-in delay-100">
            Job creation and MSME development through innovative programming
          </p>
          <a 
            href="#portfolio" 
            className="inline-flex items-center bg-primary hover:bg-secondary text-white px-8 py-4 rounded-full font-medium shadow-lg hover:shadow-xl transition transform hover:-translate-y-1 animate-fade-in delay-200"
          >
            <i className="fas fa-play-circle mr-2"></i> Watch Our Story
          </a>
        </div>
      </section>

      {/* Portfolio Section */}
      <section className="py-20 bg-white" id="portfolio">
        <div className="container mx-auto px-6">
          <div className="text-center mb-16">
            <span className="inline-block px-4 py-2 bg-primary/10 text-primary rounded-full mb-4 animate-pulse">
              Portfolio
            </span>
            <h2 className="text-4xl font-bold text-gray-900 mb-4">
              Our Service Portfolio & Partners
            </h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Collaborative programs driving job creation and MSME development in Ethiopia
            </p>
          </div>

          {/* Filter Tabs */}
          <div className={`flex flex-wrap justify-center gap-3 mb-12 sticky top-20 z-10 bg-white py-4 transition-shadow ${isScrolled ? 'shadow-md' : ''}`}>
            {categories.map(category => (
              <button
                key={category}
                onClick={() => setActiveFilter(category)}
                className={`px-5 py-2 rounded-full text-sm font-medium transition-all duration-300 ${
                  activeFilter === category
                    ? 'bg-primary text-white shadow-lg'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {category}
              </button>
            ))}
          </div>

          {/* Project Cards */}
          <div className="grid grid-cols-1 gap-10">
            {filteredProjects.map(project => (
              <div 
                key={project.readMoreKey} 
                className="bg-white rounded-2xl overflow-hidden shadow-xl transition-all duration-300 hover:shadow-2xl border border-gray-100"
              >
                <div className="md:flex">
                  {/* Project Image */}
                  <div className="md:w-1/3 relative">
                    <div className="aspect-w-16 aspect-h-9 w-full h-full">
                      <img
                        src={project.image}
                        alt={project.alt}
                        className="w-full h-full object-cover"
                        onError={e => { 
                          e.target.onerror = null; 
                          e.target.src = '/image/Biruhminds_logo.png';
                        }}
                      />
                    </div>
                    <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-4">
                      <span className="text-white text-sm font-medium">
                        {project.location}
                      </span>
                    </div>
                  </div>

                  {/* Project Content */}
                  <div className="md:w-2/3 p-8">
                    <div className="flex justify-between items-start mb-4">
                      <div>
                        <span className="inline-block bg-gray-100 text-gray-700 text-xs px-2 py-1 rounded mb-2">
                          {project.partners}
                        </span>
                        <h3 className="text-2xl font-bold text-gray-900 mb-2">
                          {project.title}
                        </h3>
                      </div>
                      {project.readMore && (
                        <button
