import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Projects = () => {
  const [expandedSections, setExpandedSections] = useState({});

  const toggleExpand = (section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  useEffect(() => {
    // Mobile menu toggle functionality
    const menuToggle = document.getElementById('menu-toggle');
    const menu = document.getElementById('mobile-menu');
    const bar1 = document.getElementById('bar1');
    const bar2 = document.getElementById('bar2');
    const bar3 = document.getElementById('bar3');

    const mobileServicesBtn = document.getElementById('mobile-services-btn');
    const mobileServicesMenu = document.getElementById('mobile-services-menu');
    const mobileResearchBtn = document.getElementById('mobile-research-btn');
    const mobileResearchMenu = document.getElementById('mobile-research-menu');
    const mobileBdsBtn = document.getElementById('mobile-bds-btn');
    const mobileBdsMenu = document.getElementById('mobile-bds-menu');
    const mobileLiyuBtn = document.getElementById('mobile-liyu-btn');
    const mobileLiyuMenu = document.getElementById('mobile-liyu-menu');

    function toggleMenu() {
      if (menu.classList.contains('max-h-0')) {
        menu.classList.remove('max-h-0');
        menu.classList.add('max-h-screen', 'py-4');
        bar1.classList.add('rotate-45', 'translate-y-2');
        bar2.classList.add('opacity-0');
        bar3.classList.add('-rotate-45', '-translate-y-2');
      } else {
        menu.classList.add('max-h-0');
        menu.classList.remove('max-h-screen', 'py-4');
        bar1.classList.remove('rotate-45', 'translate-y-2');
        bar2.classList.remove('opacity-0');
        bar3.classList.remove('-rotate-45', '-translate-y-2');
      }
    }

    if (menuToggle) {
      menuToggle.addEventListener('click', toggleMenu);
    }

    if (mobileServicesBtn && mobileServicesMenu) {
      mobileServicesBtn.addEventListener('click', () => {
        mobileServicesMenu.classList.toggle('hidden');
      });
    }
    if (mobileResearchBtn && mobileResearchMenu) {
      mobileResearchBtn.addEventListener('click', () => {
        mobileResearchMenu.classList.toggle('hidden');
      });
    }
    if (mobileBdsBtn && mobileBdsMenu) {
      mobileBdsBtn.addEventListener('click', () => {
        mobileBdsMenu.classList.toggle('hidden');
      });
    }
    if (mobileLiyuBtn && mobileLiyuMenu) {
      mobileLiyuBtn.addEventListener('click', () => {
        mobileLiyuMenu.classList.toggle('hidden');
      });
    }

    // Cleanup event listeners on unmount
    return () => {
      if (menuToggle) {
        menuToggle.removeEventListener('click', toggleMenu);
      }
      if (mobileServicesBtn && mobileServicesMenu) {
        mobileServicesBtn.removeEventListener('click', () => {
          mobileServicesMenu.classList.toggle('hidden');
        });
      }
      if (mobileResearchBtn && mobileResearchMenu) {
        mobileResearchBtn.removeEventListener('click', () => {
          mobileResearchMenu.classList.toggle('hidden');
        });
      }
      if (mobileBdsBtn && mobileBdsMenu) {
        mobileBdsBtn.removeEventListener('click', () => {
          mobileBdsMenu.classList.toggle('hidden');
        });
      }
      if (mobileLiyuBtn && mobileLiyuMenu) {
        mobileLiyuBtn.removeEventListener('click', () => {
          mobileLiyuMenu.classList.toggle('hidden');
        });
      }
    };
  }, []);

  return (
    <>
      {/* Hero Video Section */}
      <section className="relative bg-dark text-white">
        <div className="container mx-auto px-6 py-32">
          <div className="text-center">
            <h1 className="text-5xl md:text-6xl font-bold mb-4">
              Our Projects & Impact
            </h1>
            <p className="text-xl mb-8">
              Explore the initiatives and collaborations driving sustainable development.
            </p>
            <Link
              to="/contact"
              className="inline-block px-8 py-4 bg-primary text-white rounded-lg shadow-md hover:bg-primary-dark transition-all duration-300"
            >
              Get In Touch
            </Link>
          </div>

          <div className="absolute inset-0">
            <video
              className="w-full h-full object-cover"
              autoPlay
              loop
              muted
              playsInline
            >
              <source src="/video/hero-bg.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        </div>
      </section>

      {/* Portfolio Section */}
      <section className="py-20 bg-white" id="portfolio">
        <div className="container mx-auto px-6">
          <div className="text-center mb-16">
            <span className="inline-block px-4 py-2 bg-primary/10 text-primary rounded-full mb-4">Portfolio</span>
            <h2 className="text-4xl font-bold text-dark mb-4">Our Service Portfolio & Partners</h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Collaborative programs driving job creation and MSME development in Ethiopia
            </p>
          </div>

          {/* Rebuilding MSMEs After Conflict */}
          <div className="project-section bg-white rounded-xl shadow-lg gradient-border mb-20">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
              <div className="relative rounded-l-xl overflow-hidden flex items-center justify-center min-h-[400px] bg-gray-100">
                <img
                  src="/image/portfolio/eed.png"
                  alt="Rebuilding MSMEs After Conflict"
                  className="w-full h-full object-cover rounded-xl border-2 border-dashed"
                  style={{ minHeight: '400px', maxHeight: '400px' }}
                  onError={e => { e.target.onerror = null; e.target.src = '/image/Biruhminds_logo.png'; }}
                />
              </div>
              <div className="p-8">
                <div className="text-sm text-primary font-medium mb-2">Partners: Ethiopian Enterprise Development (EED)</div>
                <h3 className="text-2xl font-bold text-dark mb-4">Rebuilding MSMEs After Conflict</h3>
                <p className="text-gray-600 mb-2">
                  <strong>Intervention Area:</strong> Tigray and Amhara regions, Ethiopia
                </p>
                <p className="text-gray-600 mb-6">
                  <strong>Short Description:</strong> The EED project trained over 1,200 entrepreneurs and experts in Tigray and Amhara to revitalize conflict-affected MSMEs through leadership and entrepreneurial mindset training.
                </p>

                {/* Read More Content */}
                {expandedSections['rehab-more'] && (
                  <div className="mt-4">
                    <p className="text-gray-600 mb-4">
                      This initiative empowered 230 micro and small enterprises (MSEs) devastated by war, equipping owners and local trade experts with critical skills to rebuild their businesses. The training focused on:
                    </p>
                    <ul className="space-y-2 mb-4">
                      <li className="flex items-start">
                        <i className="fas fa-check-circle text-accent mr-2 mt-1"></i>
                        <span><strong>Resilient leadership:</strong> Helping entrepreneurs recover and adapt in post-conflict economies.</span>
                      </li>
                      <li className="flex items-start">
                        <i className="fas fa-check-circle text-accent mr-2 mt-1"></i>
                        <span><strong>Business revival strategies:</strong> Enabling MSMEs to restart operations, access markets, and sustain livelihoods.</span>
                      </li>
                      <li className="flex items-start">
                        <i className="fas fa-check-circle text-accent mr-2 mt-1"></i>
                        <span><strong>Community impact:</strong> Strengthening local economies and fostering long-term stability in Tigray and Amhara.</span>
                      </li>
                    </ul>
                  </div>
                )}
                <button
                  className={`read-more-btn inline-flex items-center text-primary font-medium hover:text-secondary mt-4 ${expandedSections['rehab-more'] ? 'expanded' : ''}`}
                  onClick={() => toggleExpand('rehab-more')}
                >
                  {expandedSections['rehab-more'] ? 'Read Less' : 'Read More'}
                </button>
              </div>
            </div>
          </div>

          {/* ...existing code for other project sections... */}
        </div>
      </section>
    </>
  );
};

export default Projects;
